import CartContext from "./CartContext";
import { useReducer } from "react";

const initial = {
  items: [],
  amount: 0,
};
function cartManager(state, action) {
  if (action.id === "add") {
    if (
      state.items.findIndex((item) => {
        return item.name === action.value.name;
      }) > -1
    ) {
      state.items[
        state.items.findIndex((item) => {
          return item.name === action.value.name;
        })
      ].amount = action.value.amount;
    } else {
      state.items = [...state.items, action.value];
    }
  }
  if (action.id === "remove") {
    console.log("remove it");
    state.items = state.items.filter((item) => {
      return item.name !== action.value.name;
    });
  }

  return initial;
}

export default function CartContextProvider(props) {
  const [cartState, cartDispacher] = useReducer(cartManager, initial);
  function addItemHandler(event) {
    cartDispacher({ value: event, id: "add" });
  }
  function removeItemHandler(event) {
    cartDispacher({ value: event, id: "remove" });
  }

  const CartValue = ({
    items: cartState.items,
    amount: cartState.amount,
    addItem: addItemHandler,
    removeItem: removeItemHandler,
  });

  return (
    <CartContext.Provider value={CartValue}>
      {props.children}
    </CartContext.Provider>
  );
}
