import Style from "./Forms.module.css";
import { useState, useContext } from "react";
import CartContext from "../Storage/CartContext";

export default function Forms(props) {
  const ctx = useContext(CartContext);
  // const [amount, setAmount] = useState(0);

  // function inputHandler(event) {
  //   setAmount(+event.target.value);
  // }
  // function clickHandler() {
  //   setAmount((previous) => previous + 1);
  // }
  function submitHandler(event) {
    event.preventDefault();
    const addItem = { amount: amount, name: props.name, price: props.price };
    ctx.addItem(addItem);
  }
  return (
    <form className={Style.form} onSubmit={submitHandler}>
      <div className={Style.input}>
        <label>Amount</label>
        <input
          type="number"
          min="0"
          max="5"
          step="1"
          value={ctx.items.findIndex((item) => {return (item.name === props.name)}) > -1 ? ctx.items[ctx.items.findIndex((item) => {return (item.name === props.name)})].amount : amount}
          
        ></input>
      </div>
      <button type="submit" >
        + Add
      </button>
    </form>
  );
}
