import Style from "./Forms.module.css"
import { useState, useContext } from "react";
import CartContext from "../UI/CartContext";


export default function Forms(props) {
    const ctx = useContext(CartContext)
    const [amount, setAmount] =useState(ctx.amount)
    function inputHandler(event){
        setAmount(+event.target.value)
    }
    function clickHandler(){
        setAmount((previous) => previous + 1 ) 
    }
    function submitHandler(event){
        event.preventDefault();
        ctx.amount = amount;
        ctx.name = props.name;
        ctx.price = props.price; 
    }
    return (
        <form className={Style.form} onSubmit={submitHandler}> {/*might make an issue and will not submit value entred from input */}
                <div className={Style.input}>
                    <label>Amount</label>
                    <input type="number" min={0} value={amount} onChange={inputHandler}></input>
                </div>
                <button type="submit" onClick={clickHandler}>+ Add</button> 
        </form>
    );
}
