import { useEffect } from "react";
import React from "react";

const DUMMY_MEALS = [
    {
      id: 'm1',
      name: 'Sushi',
      description: 'Finest fish and veggies',
      price: 22.99,
    },
    {
      id: 'm2',
      name: 'Schnitzel',
      description: 'A german specialty!',
      price: 16.5,
    },
    {
      id: 'm3',
      name: 'Barbecue Burger',
      description: 'American, raw, meaty',
      price: 12.99,
    },
    {
      id: 'm4',
      name: 'Green Bowl',
      description: 'Healthy...and green...',
      price: 18.99,
    },
  ];

const CartContext = React.createContext(
    {amount: 0, id:""}
)
useEffect(
    console.log(DUMMY_MEALS.findIndex((item) => {item.name === CartContext.id}))
    // DUMMY_MEALS[].amount = CartContext.amount

,[CartContext,DUMMY_MEALS])


export default CartContext;
