import Style from "./CartItems.module.css"
import { useContext, useState, useEffect } from "react";
import CartContext from "../UI/CartContext";

export default function CartItem(props){
    const ctx = useContext(CartContext);
    const [amount, setAmount] =useState(ctx.amount)
    useEffect({pass},[ctx.amount]);

    function addHandler(){
        setAmount((previous) => previous + 1 ) ;
        ctx.amount = amount;
    }
    function subHandler(){
        setAmount((previous) => previous - 1 ) ;
        ctx.amount = amount;
    }
    return (
        <div className={Style['cart-item']}>
            <div>
                <h2>{ctx.name}</h2>
                <div className={Style.summary}>
                    <p className={Style.price}>${Math.round(ctx.price * ctx.amount)}</p>
                    <p className={Style.amount} >x {amount}</p>
                </div>
            </div>
            <div className={Style.actions}>
                <button onClick={addHandler}>+</button>
                <button onClick={subHandler}>-</button>
            </div>
        </div>
    );
}
