import Style from "./CartItems.module.css"
import { useContext, useState } from "react";
import CartContext from "../Storage/CartContext";

export default function CartItem(props){
    const ctx = useContext(CartContext);

    const [amount, setAmount] =useState(props.amount)
    const [action , setAction] = useState(true)

    function addHandler(){
        setAmount((previous) => previous + 1 ) ;
        setAction(true);
        
    }
    function subHandler(){
        setAmount((previous) => previous - 1 ) ;
        setAction(false);
            
    }
    function submitHandler(value){
        value.preventDefault();
        if(action){
            ctx.addItem({
            name: props.name,
            amount: amount
        })
        }
        else{
            ctx.removeItem({
                name: props.name,
                amount: amount
        })
        }

    }
    return (
        <div className={Style['cart-item']}>
            <div>
                <h2>{props.name}</h2>
                <div className={Style.summary}>
                    <p className={Style.price}>${Math.round(props.price * props.amount)}</p>
                    <p className={Style.amount} >x {amount}</p>
                </div>
            </div>
            <form className={Style.actions} onSubmit={submitHandler}>
                <button type="submit" onClick={addHandler}>+</button>
                <button type = "submit" onClick={subHandler}>-</button>
            </form>
        </div>
    );
}
