// ];

function App() {
  const [popcart, setPopCart] = useState(false);
  const {error, isLoading, fetchData} = useHttp();
  const [meals, setMeals] = useState([]);

  useEffect(() => {
    function dataHandler(data){
      const menuItems = []
      for(var id in data){
        menuItems.push({id:id, name : data[id].name, description:data[id].description, price:data[id].price})
      }
      setMeals(menuItems);
    }
    fetchData({link:"https://react-f9541-default-rtdb.firebaseio.com/Menu"},dataHandler);

  },[fetchData])
  console.log(error);

  function clickHandler() { 
    fetchData()
   }

  return (
    <CartContextProvider>
      {popcart && (
        <Cart
          onClick={() => {
            setPopCart(false);
          }}
        />
      )}
      <Header
        onClick={() => {
          setPopCart(true);
        }}
      ></Header>
      <Intro></Intro>
      {error ? <p className="loading">{error}</p>:
      !isLoading ? <Menu Menu={meals}></Menu>: <Card className="loading">Loading data...</Card>}
    </CartContextProvider>
  );
}

export default App;
