import classes from "./SubmitForm.module.css";
import useInput from "../../Hooks/useInput";

export default function SubumitForm(props){

    const {value, inputStyle, showError, ChangeHandler, blurHandler, reset} = useInput();

    console.log(inputStyle);

    function confirmHandler() { 

     }

    return(
        <form className={classes.form} onSubmit={confirmHandler}>
      <div className={`${classes.control} ${classes[inputStyle]}`}>
        <label htmlFor='name'>Your Name</label>
        <input type='text' id='name' value={value} onChange={ChangeHandler} onBlur={blurHandler}/>
      </div>
      <div className={classes.control}>
        <label htmlFor='street'>Street</label>
        <input type='text' id='street' />
      </div>
      <div className={classes.control}>
        <label htmlFor='postal'>Postal Code</label>
        <input type='text' id='postal' />
      </div>
      <div className={classes.control}>
        <label htmlFor='city'>City</label>
        <input type='text' id='city' />
      </div>
      <div className={classes.actions}>
        <button type='button' onClick={props.onCacelOrder}>
          Cancel
        </button>
        <button type="submit" className={classes.submit}>Confirm</button>
      </div>
    </form>
    );
}
